mapper.select:
SELECT

FROM ua_department


        AND id = #{id,jdbcType=INTEGER}


        AND name = #{name,jdbcType=VARCHAR}


        AND superior_leader = #{superiorLeader,jdbcType=BIGINT}


        AND parent_dept_id = #{parentDeptId,jdbcType=INTEGER}


        AND tree_code = #{treeCode,jdbcType=VARCHAR}


        AND charge_cust_id = #{chargeCustId,jdbcType=BIGINT}


        AND delegate_cust_id = #{delegateCustId,jdbcType=BIGINT}


        AND department_type = #{departmentType,jdbcType=INTEGER}


        AND status = #{status,jdbcType=CHAR}


        AND gmt_created = #{gmtCreated,jdbcType=TIMESTAMP}


        AND gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}


        AND creator = #{creator,jdbcType=VARCHAR}


        AND modifier = #{modifier,jdbcType=VARCHAR}


        AND is_deleted = #{isDeleted,jdbcType=CHAR}


        AND code = #{code,jdbcType=VARCHAR}


        AND parent_business_code = #{parentBusinessCode,jdbcType=VARCHAR}


        AND business_code = #{businessCode,jdbcType=VARCHAR}


        AND company_id = #{companyId,jdbcType=BIGINT}


        AND locale_code like concat(#{localeCode,jdbcType=VARCHAR},'%')


        AND extra_info = #{extra_info,jdbcType=VARCHAR}


        AND parent_code = #{parent_code,jdbcType=VARCHAR}


        AND finance_dept_code = #{finance_dept_code,jdbcType=VARCHAR}


        AND level = #{level,jdbcType=VARCHAR}


        AND address_cname = #{addressCName,jdbcType=VARCHAR}


        AND post_code = #{postCode,jdbcType=VARCHAR}


        AND phone_number = #{phoneNumber,jdbcType=VARCHAR}


        AND center_flag = #{centerFlag,jdbcType=VARCHAR}


        AND web_address = #{webAddress,jdbcType=VARCHAR}


        AND service_phone = #{servicePhone,jdbcType=VARCHAR}


        AND com_flag = #{comFlag,jdbcType=VARCHAR}


        AND district_name = #{districtName,jdbcType=VARCHAR}


        AND district_plat_code = #{districtPlatCode,jdbcType=VARCHAR}


        AND policy_city_code = #{policyCityCode,jdbcType=VARCHAR}


        AND policy_city_code = #{policyAreaCode,jdbcType=VARCHAR}
